package TxBuild;
import java.awt.Image;
import java.io.BufferedWriter;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileWriter;
import java.util.Base64;
import javax.imageio.ImageIO;
import javax.swing.ImageIcon;






/************************************************************************************************************
* 	MyIcoins V1.0							Mr.Maxwell									31.01.2026			*
* 	Hier werden die vom Programm verwendeten Icons Hard codiert gespeichert.								*
* 	Alle Icoins könnnen von hier aus direkt als static ImageIcon geladen werden								*
* 	Die Icoins werden immer (auch ohne Aufruf) in dem static-Block erstellt.
* 	Neue Icois können hier mit der Main Methode codiert werden.												*
***********************************************************************************************************/






public class MyIcons 
{

	 public static ImageIcon fee; 
	 public static ImageIcon hex;
	 public static ImageIcon csv;
	 public static ImageIcon lock;
	 public static ImageIcon json;
	 public static ImageIcon send;
	 public static ImageIcon open;
	 public static ImageIcon save;
	 public static ImageIcon load;
	 public static ImageIcon load2;
	 public static ImageIcon chart;
	 public static ImageIcon chain;
	 public static ImageIcon viewTx;
	 public static ImageIcon qrCode;
	 public static ImageIcon qrScan;
	 public static ImageIcon cancel;
	 public static ImageIcon loadTx;
	 public static ImageIcon connect;
	 public static ImageIcon settings;
	 public static ImageIcon load_gif;
	 public static ImageIcon openCrypt;
	 public static ImageIcon txSettings;
	 public static ImageIcon qrCode42x42;
	 public static ImageIcon bitcoinLogoMain;
	 public static ImageIcon bitcoinLogoTest;
	 
	 
	 
	
	static // wird immer Ausgeführt!
	{
		fee				= Base64StringToImageIcon("");
		hex 			= Base64StringToImageIcon("");
		csv				= Base64StringToImageIcon("");
		lock			= Base64StringToImageIcon("");
		json			= Base64StringToImageIcon("");
		send			= Base64StringToImageIcon("");
		open			= Base64StringToImageIcon("");
		save 			= Base64StringToImageIcon("");
		load 			= Base64StringToImageIcon("");
		load2 			= Base64StringToImageIcon("");
		chart			= Base64StringToImageIcon("");
		chain			= Base64StringToImageIcon("");
		viewTx 			= Base64StringToImageIcon("");
		qrCode 			= Base64StringToImageIcon("");
		qrScan			= Base64StringToImageIcon("");
		cancel 			= Base64StringToImageIcon("");
		loadTx			= Base64StringToImageIcon("");
		connect			= Base64StringToImageIcon("");
		settings		= Base64StringToImageIcon("");
		load_gif		= Base64StringToGif("");
		openCrypt		= Base64StringToImageIcon("");
		txSettings		= Base64StringToImageIcon("");
		qrCode42x42		= Base64StringToImageIcon("");
		bitcoinLogoMain	= Base64StringToImageIcon("");
		bitcoinLogoTest	= Base64StringToImageIcon("");
	}
	
	
	
	
	// Diese Main-Methode ist nur zum Erstellen/Codieren neuer Icoins da.
	// Wenn ein neues Icoin codiert werden soll, muss die "meinIcon.png" Datei im icons Ordner liegen
	// Nach dem Ausführen, wird eine "meinIcon.txt" Datei erzeugt.
	// Diese Datei enthält dann den Base64 png-String, der dann unten hineinkopiert werden kann.
	public static void main(String[] args) throws Exception 
	{
		String file = "qrCode42x42";  	// <------------------- hier Name der Datei eintragen!
		
		File pngFile = new File("icons/"+file+".png");
		byte[] b = new byte[(int) pngFile.length()];
		FileInputStream fs = new FileInputStream(pngFile);
		fs.read(b);
		fs.close();
		String str=  Base64.getEncoder().encodeToString(b);
		BufferedWriter br = new BufferedWriter(new FileWriter("icons/"+file+".txt"));
		br.write(str);
		br.close();
		System.out.println("Datei als .txt gespeichert.");
	}
	
	
	
	
	
	// Konvertiert ein Base64 String in ein ImageIcon
	private static ImageIcon Base64StringToImageIcon(String str) 
	{
		try
		{
			byte[] imageBytes = Base64.getDecoder().decode(str);
			Image image = ImageIO.read(new ByteArrayInputStream(imageBytes));
			return new ImageIcon(image);
		}
		catch(Exception e)
		{
			e.printStackTrace();
		}	
		return null;
	}
	
	
	
	// Konvertiert ein Base64 String in ein GIF
	private static ImageIcon Base64StringToGif(String str) 
	{
		try 
		{
			byte[] imageBytes = Base64.getDecoder().decode(str);
			ByteArrayInputStream byteArrayInputStream = new ByteArrayInputStream(imageBytes);
			return new ImageIcon(byteArrayInputStream.readAllBytes());
		} 
		catch (Exception e) 
		{
			e.printStackTrace();
		}   
		return null;
	}
}